mapper.select:
		select *
        from (
        	select rownum as rn, tg_id, tg_brand, tg_name, tg_img, tg_text, tg_dcrate,  tg_print, tg_sedan, tg_suv
                from (
                    select * from tire_group, tire_brand
                where tg_brand like '%'||#{search}||'%'

		        		and tg_${search2} = 1

                and tg_brand = tb_name
        		order by tb_order, tg_id
            )
        )
        where rn >= #{start} and rn <= #{end}

select * from tire_item, tire_group
where tg_id = ti_tg_id
and tg_id = #{tg_id}
and ti_width = #{ti_width}
and ti_ratio = #{ti_ratio}
and ti_inch = #{ti_inch}

select * from tire_item, tire_group
where tg_id = ti_tg_id
and ti_width = #{ti_width}
and ti_ratio = #{ti_ratio}
and ti_inch = #{ti_inch}

select * from car
where #{c_year1} >= c_year1 
and #{c_year1} <= c_year2
and c_brand = #{c_brand}
and c_name = #{c_name}
